INSERT INTO `wp_posts` VALUES (206,1,'2024-04-05 00:37:43','2024-04-05 00:37:43','[et_pb_section fb_built=\"1\" theme_builder_area=\"post_content\" _builder_version=\"4.24.2\" _module_preset=\"default\"][et_pb_row _builder_version=\"4.24.2\" _module_preset=\"default\" theme_builder_area=\"post_content\" background_color=\"#000000\" hover_enabled=\"0\" sticky_enabled=\"0\"][et_pb_column _builder_version=\"4.24.2\" _module_preset=\"default\" type=\"4_4\" theme_builder_area=\"post_content\" hover_enabled=\"0\" sticky_enabled=\"0\"][et_pb_text _builder_version=\"4.24.2\" _module_preset=\"default\" theme_builder_area=\"post_content\" header_2_text_align=\"center\" header_2_text_color=\"#FFFFFF\" hover_enabled=\"0\" sticky_enabled=\"0\" header_4_text_align=\"center\" header_4_text_color=\"#FFFFFF\"]<h2>Sobre PEA2</h2>\n<h4><span>Juntos, Podemos</span></h4>[/et_pb_text][/et_pb_column][/et_pb_row][et_pb_row _builder_version=\"4.24.2\" _module_preset=\"default\" theme_builder_area=\"post_content\"][et_pb_column _builder_version=\"4.24.2\" _module_preset=\"default\" type=\"4_4\" theme_builder_area=\"post_content\"][et_pb_text _builder_version=\"4.24.2\" _module_preset=\"default\" theme_builder_area=\"post_content\" hover_enabled=\"0\" sticky_enabled=\"0\"]<h3 class=\"font_6 wixui-rich-text__text\" data-sider-select-id=\"c9e26ea1-479c-419d-8635-bff0a2a2d6d1\">Esp.</h3>\n<p class=\"font_7 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">La Asociación de Profesores de Español de Aquí y Allá (PEA2) es una asociación que existe legalmente (N°002/RDA/F.32/SASC)<span> </span>desde el 5 de enero de 2022 y tiene como principales objetivos la difusión de la lengua española y las culturas de los países hispanos en África, el apoyo del Estado camerunés en la ejecución de su política educativa en cuanto a los enfoques educativos y la formación continua del profesorado , la lucha contra todas las formas de discriminación y violencia en el medio escolar, la creación de alianzas con otras asociaciones que persigan los mismos objetivos y el mejoramiento del proceso de enseñanza/aprendizaje</span></p>[/et_pb_text][/et_pb_column][/et_pb_row][et_pb_row _builder_version=\"4.24.2\" _module_preset=\"default\" column_structure=\"1_2,1_2\" theme_builder_area=\"post_content\"][et_pb_column _builder_version=\"4.24.2\" _module_preset=\"default\" type=\"1_2\" theme_builder_area=\"post_content\"][et_pb_image src=\"http://site-pea2.local/wp-content/uploads/2024/04/45b697_6b2f1bffcc4d4290b39932b547961d3cmv2.jpg\" _builder_version=\"4.24.2\" _module_preset=\"default\" theme_builder_area=\"post_content\" alt=\"Sobre pea2\" title_text=\"Sobre pea2\" hover_enabled=\"0\" sticky_enabled=\"0\"][/et_pb_image][/et_pb_column][et_pb_column _builder_version=\"4.24.2\" _module_preset=\"default\" type=\"1_2\" theme_builder_area=\"post_content\"][et_pb_text _builder_version=\"4.24.2\" _module_preset=\"default\" theme_builder_area=\"post_content\" hover_enabled=\"0\" sticky_enabled=\"0\"]<h3 class=\"font_6 wixui-rich-text__text\" data-sider-select-id=\"b721a92c-8476-4cf7-ba92-e08259060973\">Fr.</h3>\n<p class=\"font_7 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">L\'Association de Professeurs d’Espagnol d’Ici et D’Ailleurs « PEA2 » est une  association qui existe légalement (N°002/RDA/F.32/SASC) depuis le 05 janvier 2022 et a pour principaux objectifs la diffusion de la langue espagnole et les cultures des pays hispaniques en Afrique, l’accompagnement de l\'État camerounais dans l’implémentation de sa politique éducative en matière d’approches pédagogiques et de formation continue des enseignants, la lutte contre toutes formes de discrimination et de violence en milieu scolaire, la création de partenariats avec d’autres associations visant les mêmes objectifs et l’amélioration du processus enseignement/apprentissage.</span></p>[/et_pb_text][/et_pb_column][/et_pb_row][/et_pb_section]','Sobre PEA2','','publish','closed','closed','','sobre-pea2','','','2024-04-05 00:38:00','2024-04-05 00:38:00','',0,'http://site-pea2.local/?page_id=206',0,'page','',0);
