INSERT INTO `wp_posts` VALUES (211,1,'2024-04-05 00:48:29','2024-04-05 00:48:29','[et_pb_section fb_built=\"1\" _builder_version=\"4.24.2\" _module_preset=\"default\" global_colors_info=\"{}\" theme_builder_area=\"post_content\"][et_pb_row _builder_version=\"4.24.2\" _module_preset=\"default\" background_color=\"#000000\" global_colors_info=\"{}\" theme_builder_area=\"post_content\"][et_pb_column type=\"4_4\" _builder_version=\"4.24.2\" _module_preset=\"default\" global_colors_info=\"{}\" theme_builder_area=\"post_content\"][et_pb_text _builder_version=\"4.24.2\" _module_preset=\"default\" header_2_text_align=\"center\" header_2_text_color=\"#FFFFFF\" header_4_text_align=\"center\" header_4_text_color=\"#FFFFFF\" hover_enabled=\"0\" global_colors_info=\"{}\" theme_builder_area=\"post_content\" sticky_enabled=\"0\"]<h2>Notre Equipe</h2>[/et_pb_text][/et_pb_column][/et_pb_row][/et_pb_section][et_pb_section fb_built=\"1\" theme_builder_area=\"post_content\" specialty=\"on\" _builder_version=\"4.24.2\" _module_preset=\"default\"][et_pb_column type=\"3_4\" specialty_columns=\"3\" theme_builder_area=\"post_content\"][et_pb_row_inner _builder_version=\"4.24.2\" _module_preset=\"default\" theme_builder_area=\"post_content\"][et_pb_column_inner saved_specialty_column_type=\"3_4\" _builder_version=\"4.24.2\" _module_preset=\"default\" theme_builder_area=\"post_content\"][et_pb_text _builder_version=\"4.24.2\" _module_preset=\"default\" theme_builder_area=\"post_content\" hover_enabled=\"0\" sticky_enabled=\"0\"]<h5 class=\"font_5 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">Bureau Exécutif National</span></h5>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\"><span class=\"wixGuard wixui-rich-text__text\">​</span></span></p>\n<h3 class=\"font_8 wixui-rich-text__text\">1. Président</h3>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">Dr ESSOME LELE Gislain A.</span></p>\n<h3 class=\"font_8 wixui-rich-text__text\">2. Vice-présidente</h3>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">Mme MEGNE KAKAPEN Diane Épse KUATE </span></p>\n<h3 class=\"font_8 wixui-rich-text__text\">3. Secrétaire</h3>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">M. AZEPTO Jean Baptiste</span></p>\n<h3 class=\"font_8 wixui-rich-text__text\">4. Trésorière</h3>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">Mme NGO MOMHA DORA Épse MOUNDENE</span></p>\n<h3 class=\"font_8 wixui-rich-text__text\">5. Commissaire aux comptes</h3>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">Mme. BELOBO NGONO Diane Vidalin </span></p>\n<h3 class=\"font_8 wixui-rich-text__text\">6. Censeur</h3>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">M. TEGUEDONG NGUEKEU Cédric </span></p>\n<h3 class=\"font_8 wixui-rich-text__text\">7. Chargé des affaires académiques</h3>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">Dr TSAMO DONGMO Franck Rostov</span></p>\n<h3 class=\"font_8 wixui-rich-text__text\">8. Chargé des relations avec les institutions</h3>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">Dr DONFACK SOUNNA Anicet</span></p>\n<h3 class=\"font_8 wixui-rich-text__text\">9. Chargé des relations extérieures</h3>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">M. POZADA PINEDA Juan Enrique (Pérou)</span></p>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\"><span class=\"wixGuard wixui-rich-text__text\">​</span></span></p>\n<h2 class=\"font_5 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">Bureau Exécutif Régional (BER), Association PEA2</span></h2>\n<p class=\"font_8 wixui-rich-text__text\"><span class=\"wixui-rich-text__text\">A l’issu des élections libres et transparentes organisées le 24 septembre 202